import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  IconButton,
  Slider,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import DragIndicatorIcon from '@mui/icons-material/DragIndicator';
import { transcriptService } from '../services/transcriptService';

interface CaptionStyle {
  fontSize: number;
  backgroundColor: string;
  textColor: string;
  position: 'top' | 'middle' | 'bottom';
}

interface CaptionsWindowProps {
  isOpen: boolean;
  onClose: () => void;
  currentTime: number;
  transcript: string;
}

const CaptionsWindow: React.FC<CaptionsWindowProps> = ({
  isOpen,
  onClose,
  currentTime,
  transcript,
}) => {
  const [style, setStyle] = useState<CaptionStyle>({
    fontSize: 24,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    textColor: '#ffffff',
    position: 'bottom',
  });

  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [currentSegment, setCurrentSegment] = useState<string | null>(null);

  useEffect(() => {
    transcriptService.setTranscript(transcript);
    const removeListener = transcriptService.addListener((segment) => {
      setCurrentSegment(segment?.text || null);
    });

    return () => {
      removeListener();
    };
  }, [transcript]);

  useEffect(() => {
    transcriptService.updateTime(currentTime);
  }, [currentTime]);

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    const startX = e.clientX - position.x;
    const startY = e.clientY - position.y;

    const handleMouseMove = (e: MouseEvent) => {
      setPosition({
        x: e.clientX - startX,
        y: e.clientY - startY,
      });
    };

    const handleMouseUp = () => {
      setIsDragging(false);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  if (!isOpen) return null;

  const positionStyles = {
    top: { top: style.position === 'top' ? position.y : 'auto' },
    middle: { top: '50%', transform: 'translateY(-50%)' },
    bottom: { bottom: style.position === 'bottom' ? position.y : 'auto' },
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        left: position.x,
        ...positionStyles[style.position],
        zIndex: 1000,
        width: '100%',
        maxWidth: '800px',
        margin: '0 auto',
        padding: 2,
        transition: 'all 0.3s ease',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          mb: 1,
        }}
      >
        <IconButton
          onMouseDown={handleMouseDown}
          sx={{ cursor: 'move' }}
          size="small"
        >
          <DragIndicatorIcon />
        </IconButton>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <FormControl size="small" sx={{ minWidth: 120 }}>
            <InputLabel>Position</InputLabel>
            <Select
              value={style.position}
              onChange={(e) =>
                setStyle({ ...style, position: e.target.value as 'top' | 'middle' | 'bottom' })
              }
              label="Position"
            >
              <MenuItem value="top">Top</MenuItem>
              <MenuItem value="middle">Middle</MenuItem>
              <MenuItem value="bottom">Bottom</MenuItem>
            </Select>
          </FormControl>
          <Box sx={{ width: 100 }}>
            <Typography variant="caption" gutterBottom>
              Font Size
            </Typography>
            <Slider
              value={style.fontSize}
              onChange={(_, value) => setStyle({ ...style, fontSize: value as number })}
              min={16}
              max={36}
              step={2}
              aria-label="Font Size"
            />
          </Box>
          <IconButton onClick={onClose} size="small">
            <CloseIcon />
          </IconButton>
        </Box>
      </Box>
      {currentSegment && (
        <Typography
          variant="body1"
          sx={{
            fontSize: style.fontSize,
            backgroundColor: style.backgroundColor,
            color: style.textColor,
            padding: 2,
            borderRadius: 1,
            textAlign: 'center',
            maxWidth: '100%',
            margin: '0 auto',
            textShadow: '0 2px 4px rgba(0,0,0,0.5)',
            transition: 'all 0.3s ease',
          }}
        >
          {currentSegment}
        </Typography>
      )}
    </Box>
  );
};

export default CaptionsWindow;
